/**
 * 屏幕文字几何体
 * 每个字符是一个四边形（2个三角形）
 * 存储屏幕坐标、UV、旋转角度等信息
 */
export class ScreenTextGeometry {
    constructor(parameters: any);
    parameters: any;
    /**
     * 设置文字数据
     * @param {Array} chars - 字符数据数组
     * @param {Object} atlasInfo - 字符图集信息 {char: {u, v, width, height}}
     */
    setData(chars: any[], atlasInfo: Object): void;
}
