/**
 * 窗户特效动画材质，创建动态窗户效果。
 *
 * 窗户特效模拟窗户的开关动画，支持垂直和水平两种开关方向。
 * 可以通过配置动画参数来实现不同的视觉效果，如门窗开关、遮罩动画等。
 *
 * @category Materials
 * @example
 * ```javascript
 * const material = new mapvthree.WindowMaterial({
 *     color: new THREE.Color(0xffffff),
 *     size: [1, 1],
 *     effectSize: [1, 1],
 *     speed: 1,
 *     emissive: new THREE.Color(0x001155),
 *     direction: mapvthree.WindowMaterial.DIRECTION_VERTICAL
 * });
 * ```
 */
export class WindowMaterial extends EffectCommonMaterial {
    /**
     * 垂直开关方向
     * @type {number}
     */
    static DIRECTION_VERTICAL: number;
    /**
     * 水平开关方向
     * @type {number}
     */
    static DIRECTION_HORIZONTAL: number;
    /**
     * 构造函数
     * @param {Object} parameters 材质参数
     * @param {number} [parameters.direction] 窗户动画方向，可选值有`WindowMaterial.DIRECTION_VERTICAL`和`WindowMaterial.DIRECTION_HORIZONTAL`，默认为1
     * @param {number} [parameters.speed] 窗户动画速度，默认为1
     * @param {Array<number>} [parameters.size] 整体效果尺寸大小，默认为[1, 1]，单位为米
     * @param {Array<number>} [parameters.effectSize] 窗户动画效果大小，默认为[0.5, 0.5]，单位为米
     * @param {string|number|THREE.Color} [parameters.color] 动画效果颜色，默认为0xffffff
     * @param {number} [parameters.opacity] 整体效果透明度，默认为1
     * @param {string|number|THREE.Color} [parameters.emissive] 自发光颜色，默认为0x000000（不发光）
     */
    constructor(parameters: {
        direction?: number | undefined;
        speed?: number | undefined;
        size?: number[] | undefined;
        effectSize?: number[] | undefined;
        color?: string | number | THREE.Color;
        opacity?: number | undefined;
        emissive?: string | number | THREE.Color;
    });
    name: string;
    transparent: boolean;
    fragmentShader: any;
    vertexShader: any;
}
import { EffectCommonMaterial } from './EffectCommonMaterial';
