/**
 * 水体材质，用于模拟真实的水面效果。
 *
 * 支持水下折射、岸边泡沫、菲涅耳反射、水体深度渐变、焦散等复杂水面光照效果。
 *
 * @category Materials
 * @example
 * ```javascript
 * const waterMaterial = new mapvthree.WaterMaterial({
 *     waterColor: [0.1, 0.3, 0.8],
 *     foamSpeed: 0.05,
 *     depthSoftness: 5,
 * });
 * ```
 */
export class WaterMaterial extends CommonShaderMaterial {
    static instances: Set<any>;
    static hasActiveInstances(): boolean;
    static getActiveInstances(): Set<any>;
    /**
     * 构造函数
     * @param {Object} parameters 材质参数
     * @param {Array<number>} [parameters.waterColor] 水体的主颜色（深水区域颜色），默认为[1/255, 46/255, 120/255]
     * @param {number} [parameters.foamSpeed] 泡沫流动速度，默认为0.050
     * @param {number} [parameters.offsetScale] 波浪扰动高度偏移，默认为1
     * @param {number} [parameters.depthSoftness] 水深渐变过渡，默认为5
     * @param {number} [parameters.foamDepthSoftness] 泡沫在水深维度上的衰减程度，默认为1
     * @param {number} [parameters.foamSoftness] 泡沫边缘模糊程度，默认为3
     * @param {number} [parameters.foamScale] 泡沫贴图缩放比例，默认为100
     * @param {number} [parameters.crestFoam] 波峰泡沫判断阈值，范围0-1，默认为0.70
     * @param {Array<number>} [parameters.crestFoamColor] 波峰泡沫颜色，默认为[0.3, 0.3, 0.6]
     * @param {number} [parameters.specularStrength] 高光闪耀强度，默认为1.5
     * @param {number} [parameters.uvScale] UV缩放比例，默认为1000.0
     * @param {boolean} [parameters.useEnvMap] 是否使用环境贴图，默认为true
     * @param {number} [parameters.timeScaleFactor] 时间缩放因子，默认为0.001
     */
    constructor(parameters?: {
        waterColor?: number[] | undefined;
        foamSpeed?: number | undefined;
        offsetScale?: number | undefined;
        depthSoftness?: number | undefined;
        foamDepthSoftness?: number | undefined;
        foamSoftness?: number | undefined;
        foamScale?: number | undefined;
        crestFoam?: number | undefined;
        crestFoamColor?: number[] | undefined;
        specularStrength?: number | undefined;
        uvScale?: number | undefined;
        useEnvMap?: boolean | undefined;
        timeScaleFactor?: number | undefined;
    });
    /** @private */ private _timeScaleFactor;
    /** @private */ private _style;
    /** @private */ private _useEnvMap;
    /** @private */ private _waterColor;
    /** @private */ private _foamSpeed;
    /** @private */ private _offsetScale;
    /** @private */ private _depthSoftness;
    /** @private */ private _foamDepthSoftness;
    /** @private */ private _foamSoftness;
    /** @private */ private _foamScale;
    /** @private */ private _crestFoam;
    /** @private */ private _crestFoamColor;
    /** @private */ private _specularStrength;
    /** @private */ private _uvScale;
    type: string;
    vertexShader: any;
    fragmentShader: any;
    isWaterMaterial: boolean;
    lights: boolean;
    transparent: boolean;
    fog: boolean;
    /**
     * 设置水系样式，默认为lake
     * @param {'river'|'lake'|'ocean'} style 样式名称，只接受'river', 'lake', 'ocean'
     */
    set style(style: "river" | "lake" | "ocean");
    /**
     * 水体样式
     * @type {string}
     */
    get style(): string;
    /**
     * 设置水体的主颜色（深水区域颜色）
     * @param {Array<number>} value RGB颜色数组，范围0-1
     */
    set waterColor(value: Array<number>);
    /**
     * 水体的主颜色（深水区域颜色）
     * @type {Array<number>}
     */
    get waterColor(): Array<number>;
    /**
     * 设置泡沫的流动速度
     * @param {number} value 泡沫流动速度
     */
    set foamSpeed(value: number);
    /**
     * 控制泡沫的流动速度（用于 `tFoamNoise` 采样偏移）
     * @type {number}
     */
    get foamSpeed(): number;
    /**
     * 设置波浪扰动高度偏移
     * @param {number} value 波浪扰动高度偏移
     */
    set offsetScale(value: number);
    /**
     * 控制波浪扰动高度偏移，默认为1
     * @type {number}
     */
    get offsetScale(): number;
    /**
     * 设置水深渐变过渡
     * @param {number} value 水深渐变过渡值
     */
    set depthSoftness(value: number);
    /**
     * 控制水深渐变过渡，默认为5
     * @type {number}
     */
    get depthSoftness(): number;
    /**
     * 设置泡沫在水深维度上的衰减程度
     * @param {number} value 泡沫深度衰减值
     */
    set foamDepthSoftness(value: number);
    /**
     * 控制泡沫在水深维度上的衰减程度，泡沫从深度为0到foamDepthSoftness范围衰减，默认为1
     * @type {number}
     */
    get foamDepthSoftness(): number;
    /**
     * 设置泡沫边缘模糊的程度
     * @param {number} value 泡沫边缘模糊值
     */
    set foamSoftness(value: number);
    /**
     * 控制泡沫边缘模糊的程度
     * @type {number}
     */
    get foamSoftness(): number;
    /**
     * 设置泡沫贴图缩放比例
     * @param {number} value 泡沫贴图缩放比例
     */
    set foamScale(value: number);
    /**
     * 控制泡沫贴图缩放比例，默认为100
     * @type {number}
     */
    get foamScale(): number;
    /**
     * 设置波峰泡沫的判断阈值
     * @param {number} value 波峰泡沫阈值，范围0-1
     */
    set crestFoam(value: number);
    /**
     * 控制波峰泡沫的判断阈值，1最大，默认0.7
     * @type {number}
     */
    get crestFoam(): number;
    /**
     * 设置波峰泡沫颜色
     * @param {Array<number>} value RGB颜色数组，范围0-1
     */
    set crestFoamColor(value: Array<number>);
    /**
     * 波峰泡沫颜色
     * @type {Array<number>}
     */
    get crestFoamColor(): Array<number>;
    /**
     * 设置高光闪耀强度
     * @param {number} value 高光强度值
     */
    set specularStrength(value: number);
    /**
     * 高光闪耀强度
     * @type {number}
     */
    get specularStrength(): number;
    /**
     * 设置UV缩放比例
     * @param {number} value UV缩放比例
     */
    set uvScale(value: number);
    /**
     * UV缩放比例
     * @type {number}
     */
    get uvScale(): number;
    defines: {
        CUBEUV_TEXEL_WIDTH: number;
        CUBEUV_TEXEL_HEIGHT: number;
        CUBEUV_MAX_MIP: string;
    };
    /**
     * 渲染前的处理函数，更新相机和渲染状态相关的uniforms
     * @param {Engine} engine 引擎实例
     * @param {Scene} scene 场景对象
     * @param {Camera} camera 相机对象
     * @param {Object} renderState 渲染状态
     * @protected
     */
    protected onBeforeSceneRender(engine: Engine, scene: Scene, camera: Camera, renderState: Object): void;
    /**
     * 设置是否使用环境贴图
     * @param {boolean} value 是否启用环境贴图
     */
    set useEnvMap(value: boolean);
    /**
     * 是否使用环境贴图
     * @type {boolean}
     */
    get useEnvMap(): boolean;
    /**
     * 设置时间缩放因子
     * @param {number} value 时间缩放因子
     */
    set timeScaleFactor(value: number);
    /**
     * 时间缩放因子
     * @type {number}
     */
    get timeScaleFactor(): number;
    normalMap: any;
    /**
     * 释放资源
     * 清理所有纹理和缓存
     */
    dispose(): void;
}
import { CommonShaderMaterial } from '../CommonShaderMaterial';
