/**
 * 屏幕文字材质
 */
export class ScreenTextMaterial {
    constructor(parameters?: {});
    vertexShader: any;
    fragmentShader: any;
    name: string;
    uniforms: any;
    transparent: boolean;
    depthTest: boolean;
    depthWrite: boolean;
    /**
     * 将十六进制颜色转换为 RGBA 数组
     * @private
     */
    private _hexToRgba;
    /**
     * 将 RGBA 数组转换为十六进制颜色
     * @private
     */
    private _rgbaToHex;
    _defineProxyProperties(properties: any): void;
    _defineColorProperties(): void;
}
