/**
 * 3D热力图，立体热力图，平面热力图见 {@link Heatmap}
 *
 * **注意：3D热力图目前只适用于小面积覆盖范围（区、乡镇级别以下），在大面积覆盖场景下（省、市级别以上）会有明显性能问题**
 *
 * @category Objects
 * @example
 * ```javascript
 * const heatmap = engine.add(new mapvthree.Heatmap3D({
 *     gradient: {
 *         0.4: 'rgba(0,0,255,1)',
 *         0.6: 'rgba(0,255,0,1)',
 *         0.8: 'rgba(255,255,0,1)',
 *         1: 'rgba(255,0,0,1)'
 *     },
 *     radius: 100,
 *     maxValue: 1
 * }));
 * let data = mapvthree.GeoJSONDataSource.fromGeoJSON(data);
 * heatmap.dataSource = data;
 * ```
 * ![](media://heatmap3d.png)
 */
export class Heatmap3D extends GeoMesh {
    /**
     * 构造函数
     * @param {Object} parameters 传入参数对象
     * @param {Object} [parameters.gradient] 热力渐变颜色配置，格式为{stop: 'color'}，例如{0.4: 'blue', 0.65: 'lime', 1: 'red'}
     * @param {number} [parameters.opacity=1] 透明度整体系数
     * @param {number} [parameters.heightRatio=100] 高度系数
     * @param {number} [parameters.maxValue=1] 最大热力值
     * @param {number} [parameters.radius=100] 热力绘制半径
     */
    constructor(parameters: {
        gradient?: Object | undefined;
        opacity?: number | undefined;
        heightRatio?: number | undefined;
        maxValue?: number | undefined;
        radius?: number | undefined;
    });
    /** @private */ private _gradient;
    /** @private */ private _radius;
    /** @private */ private _maxValue;
    /** @protected */ protected isHeatmap3D: boolean;
    /** @protected */ protected frustumCulled: boolean;
    /** @protected */ protected geometry: any;
    /** @protected */ protected material: any;
    /** 透明度整体系数 */ opacity: any;
    /** 高度系数 */ heightRatio: any;
    parameters: {
        gradient?: Object | undefined;
        opacity?: number | undefined;
        heightRatio?: number | undefined;
        maxValue?: number | undefined;
        radius?: number | undefined;
    };
    texture: any;
    set gradient(value: Object | {
        0.4: string;
        0.6: string;
        0.8: string;
        1: string;
    });
    /** 热力渐变颜色配置，格式为{stop: 'color'}，例如{0.4: 'blue', 0.65: 'lime', 1: 'red'} */
    get gradient(): Object | {
        0.4: string;
        0.6: string;
        0.8: string;
        1: string;
    };
    set radius(value: number);
    /** 热力绘制半径 */
    get radius(): number;
    set maxValue(value: number);
    /** 最大热力值 */
    get maxValue(): number;
}
import { GeoMesh } from '../GeoMesh';
