/**
 * 气球点，用于显示气球样式的点数据可视化，支持实例化渲染，适合大量点数据的展示。
 *
 * @category Objects
 * @example
 * ```javascript
 * // 创建气球点
 * const ballonPoint = engine.add(new mapvthree.BallonPoint({
 *     color: 0xff0000,
 *     size: 20,
 *     height: 100,
 *     iconSrc: 'car'
 * }));
 * ```
 */
export class BallonPoint extends GeoInstancedMesh {
    /**
     * 构造函数
     * @param {Object} parameters 配置参数
     * @param {string} [parameters.iconSrc='car'] 气球图标源
     * @param {number} [parameters.color] 气球颜色
     * @param {number} [parameters.size] 气球大小
     * @param {Array<number>} [parameters.size3] 气球三维大小
     * @param {number} [parameters.height] 气球高度
     * @param {number} [parameters.opacity] 透明度
     * @param {boolean} [parameters.dashed] 是否使用虚线样式
     */
    constructor(parameters: {
        iconSrc?: string | undefined;
        color?: number | undefined;
        size?: number | undefined;
        size3?: number[] | undefined;
        height?: number | undefined;
        opacity?: number | undefined;
        dashed?: boolean | undefined;
    });
    parameters: {
        iconSrc?: string | undefined;
        color?: number | undefined;
        size?: number | undefined;
        size3?: number[] | undefined;
        height?: number | undefined;
        opacity?: number | undefined;
        dashed?: boolean | undefined;
    };
    /**
     * 获取默认参数
     * @returns {Object} 默认参数对象
     * @internal
     */
    getDefaultParams(): Object;
    /**
     * 获取气球图标源
     * @param {string} iconSrc 图标源
     * @returns {string} 处理后的图标源
     * @private
     */
    private getBallonIconSrc;
}
import { GeoInstancedMesh } from '../GeoInstancedMesh';
