/**
 * 特效模型点，支持加载自定义模型作为点类数据可视化，提供大小和动画配置参数，以实现理想的动画效果。
 *
 * 注意：动画效果需要在处理画引擎时设置`rendering.enableAnimationLoop=true`
 *
 * @category Objects
 * @example
 * ```javascript
 * const effectModelPoint = engine.add(new mapvthree.EffectModelPoint({
 *     normalize: true,
 *     rotateToZUp: true,
 *     keepSize: true,
 *     animationRotate: true,
 *     animationJump: true,
 *     animationJumpHeight: 100
 * }));
 * effectModelPoint.model = gltf.scene;
 * effectModelPoint.size = 30;
 * ```
 */
export class EffectModelPoint extends GeoObject {
    /**
     * 构造函数
     * @param {Object} parameters 传入参数对象
     * @param {boolean} [parameters.normalize=true] 是否归一化模型大小
     * @param {boolean} [parameters.rotateToZUp=true] 将模型调整至z轴朝上
     * @param {boolean} [parameters.keepSize=true] 是否保持大小
     * @param {boolean} [parameters.animationRotate=false] 是否启用旋转动画
     * @param {number} [parameters.animationRotatePeriod=3000] 旋转一周的时间，单位毫秒
     * @param {boolean} [parameters.animationJump=false] 是否启用跳跃动画
     * @param {number} [parameters.animationJumpPeriod=3000] 跳跃一次的时间周期，单位毫秒
     * @param {number} [parameters.animationJumpHeight=30] 跳跃高度
     * @param {boolean} [parameters.vertexColors=false] 是否使用顶点颜色
     * @param {boolean} [parameters.vertexSizes=false] 是否使用顶点大小
     */
    constructor(parameters: {
        normalize?: boolean | undefined;
        rotateToZUp?: boolean | undefined;
        keepSize?: boolean | undefined;
        animationRotate?: boolean | undefined;
        animationRotatePeriod?: number | undefined;
        animationJump?: boolean | undefined;
        animationJumpPeriod?: number | undefined;
        animationJumpHeight?: number | undefined;
        vertexColors?: boolean | undefined;
        vertexSizes?: boolean | undefined;
    });
    /** @private */ private _model;
    /** @private */ private _normalize;
    /** @private */ private _rotateToZUp;
    /** @private */ private _keepSize;
    /** @private */ private _size;
    /** @private */ private _size3;
    /** @private */ private _useSize3;
    /** @private */ private _height;
    /** @private */ private _animationRotate;
    /** @private */ private _animationRotatePeriod;
    /** @private */ private _animationJump;
    /** @private */ private _animationJumpPeriod;
    /** @private */ private _animationJumpHeight;
    /**
     * 是否支持事件实体
     * @type {boolean}
     */
    isEventEntitySupported: boolean;
    parameters: {
        normalize?: boolean | undefined;
        rotateToZUp?: boolean | undefined;
        keepSize?: boolean | undefined;
        animationRotate?: boolean | undefined;
        animationRotatePeriod?: number | undefined;
        animationJump?: boolean | undefined;
        animationJumpPeriod?: number | undefined;
        animationJumpHeight?: number | undefined;
        vertexColors?: boolean | undefined;
        vertexSizes?: boolean | undefined;
    };
    /**
     * 加载默认模型，多个实例共享一个模型模版
     * @private
     */
    private _initDefaultModel;
    /**
     * 更新模型
     * @private
     */
    private _updateModel;
    /** @private */
    private _cleanOldMesh;
    /** @private */
    private _defineChildrenMaterialProperty;
    /**
     * effectModelPoint绑定的模型,默认为null
     */
    set model(value: any);
    get model(): any;
}
