/**
 * 特效点，根据类型自动创建对应的特效对象。
 *
 * 支持扇形雷达、气泡等特效类型，通过type字段自动创建对应的特效对象，包括`Fan`、`Bubble`、`Wave`、`Breath`、`Radar`、`RadarLayered`、`RadarSpread`类型。
 *
 * 注意：动画效果需要在处理画引擎时设置`rendering.enableAnimationLoop=true`
 *
 * @category Objects
 * @example
 * ```javascript
 * // 创建扇形雷达特效
 * const effectPoint = engine.add(new mapvthree.EffectPoint({
 *     type: 'Fan',
 *     color: 0xff0000,
 *     size: 50
 * }));
 *
 * // 创建气泡特效
 * const bubblePoint = engine.add(new mapvthree.EffectPoint({
 *     type: 'Bubble',
 *     color: 0x00ff00,
 *     size: 30
 * }));
 * ```
 */
export class EffectPoint extends GeoInstancedPointMesh {
    /**
     * 构造函数
     * @param {{color?: string, size?: number, duration?: number, type?: string, keepSize?: boolean
     * vertexColors?: boolean, vertexSizes?: boolean, segmentAngle?: number,
     * sideColor?: string}} parameters 传入参数对象
     * @param {string} parameters.color 气泡颜色
     * @param {number} parameters.size 气泡尺寸
     * @param {number} parameters.duration 气泡动画间隔时长
     * @param {string} parameters.type 气泡类型
     * @param {number} parameters.keepSize 是否保持大小
     * @param {boolean} parameters.vertexColors 是否通过数据携带颜色配置
     * @param {boolean} parameters.vertexSizes 是否通过数据携带尺寸配置
     * @param {number} parameters.segmentAngle 雷达的扇形弧度值，仅在type为Radar时有效
     * @param {string} parameters.sideColor 最外圈底色，仅在type为RadarLayered时有效
     */
    constructor(parameters: {
        color?: string;
        size?: number;
        duration?: number;
        type?: string;
        keepSize?: boolean;
        vertexColors?: boolean;
        vertexSizes?: boolean;
        segmentAngle?: number;
        sideColor?: string;
    });
    /** 气泡颜色 */ color: any;
    /** 气泡尺寸 */ size: any;
    /** 气泡动画间隔时长 */ duration: any;
    /** 气泡类型 */ type: any;
    parameters: {
        color?: string;
        size?: number;
        duration?: number;
        type?: string;
        keepSize?: boolean;
        vertexColors?: boolean;
        vertexSizes?: boolean;
        segmentAngle?: number;
        sideColor?: string;
    };
    clone(): any;
}
import { GeoInstancedPointMesh } from './GeoInstancedPointMesh';
