/**
 * 烟花特效，通过该类可以配置产生绚烂的烟花效果，支持自定义颜色、高度、速度等参数。
 *
 * @category Objects
 * @example
 * ```javascript
 * const spark = engine.add(new mapvthree.Spark({
 *     color: 0xff0cc0, // 烟花颜色
 *     height: 200, // 烟花高度
 *     speed: 0.1, // 动画速度
 *     idle: 500, // 动画的间隔时间
 *     tailLength: 80, // 动画拖尾长度
 *     emissive: new Color(0x0000ff)
 * }));
 * ```
 * ![](media://spark.png)
 */
export class Spark extends GeoLine {
    /**
     * 构造函数
     * @param {Object} parameters 传入参数对象
     * @param {string} [parameters.color] 烟花颜色
     * @param {boolean} [parameters.vertexColors] 是否通过数据携带颜色配置
     * @param {number} [parameters.height] 烟花高度
     * @param {boolean} [parameters.vertexHeights] 是否通过数据携带高度配置
     * @param {number} [parameters.speed] 动画速度
     * @param {number} [parameters.tailLength] 动画拖尾长度
     * @param {number} [parameters.idle] 动画的间隔时间，单位：毫秒
     * @param {number} [parameters.opacity] 不透明度
     */
    constructor(parameters: {
        color?: string | undefined;
        vertexColors?: boolean | undefined;
        height?: number | undefined;
        vertexHeights?: boolean | undefined;
        speed?: number | undefined;
        tailLength?: number | undefined;
        idle?: number | undefined;
        opacity?: number | undefined;
    });
    /** @protected */ protected geometry: any;
    /** @protected */ protected material: any;
    /** 烟花颜色 */ color: any;
    /** 是否通过数据携带颜色配置 */ vertexColors: any;
    /** 烟花高度 */ height: any;
    /** 是否通过数据携带高度配置 */ vertexHeights: any;
    /** 动画速度 */ speed: any;
    /** 动画拖尾长度 */ tailLength: any;
    /** 动画的间隔时间，单位：毫秒 */ idle: any;
    /** 自发光 */ emissive: any;
    parameters: {
        color?: string | undefined;
        vertexColors?: boolean | undefined;
        height?: number | undefined;
        vertexHeights?: boolean | undefined;
        speed?: number | undefined;
        tailLength?: number | undefined;
        idle?: number | undefined;
        opacity?: number | undefined;
    };
    /**
     * 获取默认参数
     * @returns {Object} 默认参数对象
     * @internal
     */
    getDefaultParams(): Object;
}
import { GeoLine } from '../GeoLine';
