/**
 * 填充多边形（不含描边），可沿Z轴拉伸高度成多边形柱状体
 *
 * 支持以下功能：
 * - 基础多边形填充渲染
 * - Z轴拉伸创建3D柱状体
 * - 贴地渲染（Ground Primitive）
 * - 纹理贴图支持
 * - 顶点颜色和高度配置
 *
 * @category Objects
 * @example
 * ```javascript
 * // 创建基础多边形
 * const polygon = engine.add(new mapvthree.Polygon({
 *     vertexColors: true,
 *     extrude: true,
 *     extrudeValue: 100,
 * }));
 *
 * // 设置数据源
 * let data = mapvthree.GeoJSONDataSource.fromGeoJSON(data);
 * data.defineAttribute('color', 'color');
 * polygon.dataSource = data;
 * ```
 *
 * @example
 * ```javascript
 * // 创建贴地多边形
 * const groundPolygon = engine.add(new mapvthree.Polygon({
 *     isGroundPrimitive: true,
 *     color: 'rgba(255, 0, 0, 0.5)',
 *     opacity: 0.8
 * }));
 * ```
 * ![](media://polygon.png)
 */
export class Polygon extends GeoMesh {
    /**
     * 创建多边形渲染对象
     * @param {Object} parameters 传入参数对象
     * @param {boolean} [parameters.extrude=false] 是否拉伸，默认否
     * @param {number} [parameters.extrudeValue=100] 拉伸高度，默认100
     * @param {boolean} [parameters.vertexHeights=false] 是否通过数据携带高度配置，默认否
     * @param {boolean} [parameters.enableBottomFace=true] 是否封闭底面
     * @param {string} [parameters.color='#ffffff'] 填充颜色
     * @param {boolean} [parameters.vertexColors=false] 是否通过数据携带颜色配置，默认否
     * @param {string} [parameters.emissive='#000000'] 自发光颜色
     * @param {number} [parameters.opacity=1] 整体透明度系数，默认1
     * @param {string} [parameters.mapSrc=''] 纹理贴图，默认''
     * @param {number} [parameters.mapScale=1] 贴图的缩放大小，仅在使用纹理贴图时有效，默认1
     * @param {number} [parameters.zOffset=0] 整体高度抬升
     * @param {number} [parameters.normalOffset=0] 沿法线方向抬升
     * @param {boolean} [parameters.perPositionHeight=true] 使用顶点高度，若不指定，则即使设置了高度也当0处理, 默认为true
     * @param {boolean} [parameters.isGroundPrimitive=false] 是否为贴地渲染
     * @param {boolean} [parameters.isDynamic=false] 是否需要动态调整数据源，贴地时使用
     * @param {Array} [parameters.excludeElements=[]] 排除的元素列表
     * @param {number} [parameters.renderOrder=0] 渲染顺序
     * @param {number} [parameters.nearScale=5] 深度图near值缩放系数
     * @example
     * ```javascript
     * const polygon = new mapvthree.Polygon({
     *     extrude: true,
     *     extrudeValue: 150,
     *     color: 'rgba(255, 0, 0, 0.8)',
     *     vertexColors: true,
     *     opacity: 0.9
     * });
     * ```
     */
    constructor(parameters?: {
        extrude?: boolean | undefined;
        extrudeValue?: number | undefined;
        vertexHeights?: boolean | undefined;
        enableBottomFace?: boolean | undefined;
        color?: string | undefined;
        vertexColors?: boolean | undefined;
        emissive?: string | undefined;
        opacity?: number | undefined;
        mapSrc?: string | undefined;
        mapScale?: number | undefined;
        zOffset?: number | undefined;
        normalOffset?: number | undefined;
        perPositionHeight?: boolean | undefined;
        isGroundPrimitive?: boolean | undefined;
        isDynamic?: boolean | undefined;
        excludeElements?: any[] | undefined;
        renderOrder?: number | undefined;
        nearScale?: number | undefined;
    });
    /**
     * 是否支持返回的事件参数中携带entity实体数据
     * @type {boolean}
     * @readonly
     */
    readonly isEventEntitySupported: boolean;
    /** @protected */ protected geometry: any;
    /** @protected */ protected material: any;
    /**
     * 是否拉伸多边形
     * @type {boolean}
     * @example
     * ```javascript
     * polygon.extrude = true;
     * ```
     */
    extrude: boolean;
    /**
     * 拉伸高度值
     * @type {number}
     * @example
     * ```javascript
     * polygon.extrudeValue = 200;
     * ```
     */
    extrudeValue: number;
    /**
     * 填充颜色
     * @type {string}
     * @example
     * ```javascript
     * polygon.color = 'rgba(255, 0, 0, 0.8)';
     * ```
     */
    color: string;
    /**
     * 是否通过数据携带颜色配置
     * @type {boolean}
     * @example
     * ```javascript
     * polygon.vertexColors = true;
     * ```
     */
    vertexColors: boolean;
    /**
     * 自发光颜色
     * @type {string}
     * @example
     * ```javascript
     * polygon.emissive = 'rgba(0, 255, 0, 0.5)';
     * ```
     */
    emissive: string;
    /**
     * 整体透明度系数
     * @type {number}
     * @example
     * ```javascript
     * polygon.opacity = 0.7;
     * ```
     */
    opacity: number;
    /**
     * 贴图的缩放大小，仅在使用纹理贴图时有效
     * @type {number}
     * @example
     * ```javascript
     * polygon.mapScale = 2.0;
     * ```
     */
    mapScale: number;
    isPolygon: boolean;
    parameters: {
        extrude?: boolean | undefined;
        extrudeValue?: number | undefined;
        vertexHeights?: boolean | undefined;
        enableBottomFace?: boolean | undefined;
        color?: string | undefined;
        vertexColors?: boolean | undefined;
        emissive?: string | undefined;
        opacity?: number | undefined;
        mapSrc?: string | undefined;
        mapScale?: number | undefined;
        zOffset?: number | undefined;
        normalOffset?: number | undefined;
        perPositionHeight?: boolean | undefined;
        isGroundPrimitive?: boolean | undefined;
        isDynamic?: boolean | undefined;
        excludeElements?: any[] | undefined;
        renderOrder?: number | undefined;
        nearScale?: number | undefined;
    };
    _nearScale: number;
    isGroundPrimitive: boolean | undefined;
    isDynamic: boolean;
    needReMapUv: boolean | undefined;
    excludeElements: any[];
    _sharedResourcesId: string;
    renderOrder: number;
    depthRenderTarget: any;
    /**
     * 场景渲染前的钩子函数，用于生成深度图
     * @param {Engine} engine 引擎实例
     * @param {Scene} scene 场景对象
     * @param {Camera} camera 相机对象
     * @param {Object} renderState 渲染状态
     * @protected
     */
    protected onBeforeSceneRenderHook(engine: Engine, scene: Scene, camera: Camera, renderState: Object): void;
    getNormal(): any;
    /**
     * 计算矩形边界
     * @returns {Array<number>} 边界数组 [minX, minY, maxX, maxY]
     * @private
     */
    private computeRectangleBounds;
    /**
     * 几何体更新后的处理
     * @protected
     */
    protected afterGeometryUpdate(): void;
    /**
     * 根据面索引和点索引获取实体索引
     * @param {number} faceIndex 面索引
     * @param {number} pointIndex 点索引
     * @returns {number} 实体索引
     * @protected
     */
    protected getEntityIndexByFace(faceIndex: number, pointIndex: number): number;
    /**
     * 根据排除元素获取共享资源ID
     * @param {Array} excludeElements 排除的元素列表
     * @returns {string} 共享资源ID
     * @private
     */
    private getSharedResourceFromExcludeElement;
    /**
     * 设置near缩放值
     * @param {number} value near缩放值
     */
    set nearScale(value: number);
    /**
     * 控制near的值，默认5，在一些对深度要求比较高的场景需要调整near的值
     * @type {number}
     * @example
     * ```javascript
     * polygon.nearScale = 10; // 提高深度精度
     * ```
     */
    get nearScale(): number;
    /**
     * 设置纹理贴图路径
     * @param {string} value 纹理贴图路径
     */
    set mapSrc(value: string);
    /**
     * 纹理贴图图片路径
     * @type {string}
     * @example
     * ```javascript
     * polygon.mapSrc = 'textures/ground.jpg';
     * ```
     */
    get mapSrc(): string;
    raycast: any;
    _computeIntersections: any;
    getVertexPosition: any;
}
import { GeoMesh } from '../GeoMesh';
