/**
 * 柱状体，支持柱形和锥形两种形状。
 *
 * @category Objects
 * @example
 * ```javascript
 * // 创建柱形
 * const pillar = engine.add(new mapvthree.Pillar({
 *     shape: 'pillar',
 *     color: 0xff0000,
 *     height: 100,
 *     radius: 10
 * }));
 *
 * // 创建锥形
 * const cone = engine.add(new mapvthree.Pillar({
 *     shape: 'cone',
 *     color: 0x00ff00,
 *     height: 80,
 *     radius: 15
 * }));
 * ```
 */
export class Pillar extends GeoInstancedPointMesh {
    /**
     * 构造函数
     * @param {Object} parameters 配置参数
     * @param {string} [parameters.shape='pillar'] 形状类型，支持'pillar'（柱形）和'cone'（锥形）
     *
     * @param {string} [parameters.color] 柱体颜色，支持十六进制颜色值或颜色名称，默认为[80, 20, 170, 0.8]
     * @param {number} [parameters.opacity] 整体透明度，取值范围0-1，默认为1
     * @param {number} [parameters.height] 柱体高度，默认为1
     * @param {number} [parameters.radius] 柱体半径，默认为6
     * @param {number} [parameters.radialSegments] 柱体边数，默认为4
     * @param {number} [parameters.heightSegments] 柱体高度分段数，默认为1
     * @param {boolean} [parameters.openEnded] 圆柱顶底是否打开，默认为true
     * @param {boolean} [parameters.vertexHeights] 是否通过数据携带高度配置，默认为false
     * @param {Object} [parameters.gradient] 颜色渐变配置对象
     * @param {boolean} [parameter.colorMode] 颜色模式, 包含'gradient'(渐变)和'band'(颜色分带)
     * @param {boolean} [parameter.heatmap] 是否开启热力模式, 默认开启
     */
    constructor(parameter: any);
    /** 整体透明度 */ opacity: any;
    /** 柱体高度 */ height: any;
    /** 柱体边数 */ radiusSegments: any;
    /** 柱体半径 */ radius: any;
    /** 颜色渐变 */ gradient: any;
    /** 是否通过数据携带高度配置 */ vertexHeights: any;
    /** 圆柱顶底是否打开 */ openEnded: any;
    /** 着色模式 */ colorMode: any;
    /** 是否开启热力模式，默认开启 */ heatmap: any;
    /**
     * @protected
     * 计算根据height和size计算模型矩阵模型矩阵
     */
    protected getInstanceLocalMatrix: (coordinates: any, dataItem: any, index: any) => any;
    /**
     * @internal
     * 计算最大高度
     */
    addCustomAttributes: () => number | undefined;
    /**
     * @internal
     * 顶部小圆球第一个顶点的索引
     */
    getSphereIndex(): void;
}
import { GeoInstancedPointMesh } from '../point/GeoInstancedPointMesh';
