/**
 * 路径追踪器，用于沿指定路径进行相机或对象的动画追踪
 *
 * `PathTracker` 支持多种路径数据格式，包括坐标数组、GeoJSON和带帧信息的对象数组。
 * 可以设置不同的视图模式（跟随、锁定、关键帧等）来实现各种追踪效果。
 *
 * @category Camera
 * @extends TrackerAbstract
 * @example
 * ```javascript
 * // 基本用法 - 沿路径追踪相机
 * const tracker = engine.add(new mapvthree.PathTracker());
 *
 * // 设置路径数据（坐标数组格式）
 * tracker.track = [
 *     [112.368264, 23.176959, 38.553634],
 *     [112.370264, 23.178959, 40.553634],
 *     [112.372264, 23.180959, 42.553634]
 * ];
 *
 * // 开始追踪
 * tracker.start({
 *     duration: 10000,
 *     pitch: 60,
 *     range: 100
 * });
 * ```
 * @example
 * ```javascript
 * // 对象追踪 - 让3D模型沿路径移动
 * const model = engine.add(new mapvthree.SimpleModel({
 *     url: 'path/to/model.glb'
 * }));
 *
 * const tracker = engine.add(new mapvthree.PathTracker());
 * tracker.track = pathCoordinates;
 * tracker.object = model; // 设置要追踪的对象
 * tracker.start({
 *     duration: 8000,
 *     range: 50
 * });
 * ```
 */
export class PathTracker extends TrackerAbstract {
    /** @private */ private _sampledPath;
    /** @private */ private _arcInfo;
    /** @private */ private _interpolateDirectThreshold;
    /** @private */ private _interpolateDirectThresholdPercent;
    updatePositionByPercentage(ratio: any): {
        point: any;
        hpr: {
            heading: any;
            pitch: any;
            roll: any;
        };
    } | undefined;
    _interpolatePath(ratio: any): {
        point: any;
        hpr: {
            heading: any;
            pitch: any;
            roll: any;
        };
    };
    _handleFrame(lastFrame: any, currentFrame: any, last: any, current: any, next: any, point: any, transPercent: any, percent: any, ratio: any): {
        point: any;
        hpr: {
            heading: number;
            pitch: number;
            roll: number;
        };
        direction: any;
    };
    _getSegment(ratio: any, dataLength: any): {
        last: any;
        current: any;
        next: any;
        isStart: boolean;
        isEnd: boolean;
        segmentPercent: number;
        lastFrame: any;
        currentFrame: any;
    };
    _computeTransition(percent: any, last: any, current: any, next: any, isStart: any, isEnd: any): {
        isTransition: any;
        transPercent: number;
    };
    _updatePositionAndDirection(last: any, current: any, percent: any, isTrans: any, transPercent: any): {
        point: any;
        hpr: {
            heading: any;
            pitch: any;
            roll: any;
        };
    };
    _findCurrentSegment(ratio: any): {
        last: any;
        current: any;
        next: any;
        percent: number;
        lastFrame: null;
        currentFrame: null;
        index: number;
    } | null;
    _calculateLocation(last: any, current: any, percent: any): {
        point: any[];
        direction: any;
    };
    _createSampledPath(path: any): {
        percent: string;
        point: any;
        position: any;
        distance: number;
        direction: any;
        pitch: number;
    }[] | undefined;
    /**
     * @private
     * 计算两点间的地图倾斜角度
     */
    private _getRadians;
    _parseCoordinateArray(target: any): {
        track: any[];
        frameInfo: never[];
    } | undefined;
    _parseGeoJSON(target: any): {
        track: any;
        frameInfo: any;
    } | undefined;
    _parseFrameArray(target: any): {
        track: any[][];
        frameInfo: {
            pitch: any;
            yaw: any;
            aim: any;
            speed: any;
            time: any;
        }[];
    } | undefined;
    /**
     * 设置路径数据
     *
     * 支持多种数据格式：
     * - ***坐标数组***：[[lng, lat, alt], [lng, lat, alt], ...]
     * - ***GeoJSON***：{geometry: {type: 'LineString', coordinates: [...]}, properties: {frameInfo: [...]}}
     * - ***帧信息数组***：[{x, y, z, yaw, pitch, speed, time}, ...]
     *
     * @param {Array|Object} target 路径数据
     * @example
     * ```javascript
     * // 坐标数组格式
     * tracker.track = [
     *     [112.368264, 23.176959, 38.553634],
     *     [112.370264, 23.178959, 40.553634]
     * ];
     * ```
     */
    set track(target: any[] | Object);
    /**
     * 获取当前路径数据
     * @returns {Array|null} 路径数据
     */
    get track(): any[] | null;
    /**
     * 设置插值直接阈值
     * 用于控制路径插值的平滑程度，值越大路径拐角过渡越平滑，但也会越偏离实际路线
     * @param {number} value 阈值
     */
    set interpolateDirectThreshold(value: number);
    /**
     * 获取插值直接阈值
     * @returns {number} 当前阈值
     */
    get interpolateDirectThreshold(): number;
    /**
     * 设置轨迹点插值方式
     * @param {string} value 插值方式，可选值：'curve'（曲线插值）
     * @example
     * ```javascript
     * // 启用曲线插值，使路径更加平滑
     * tracker.pointHandle = 'curve';
     * ```
     */
    set pointHandle(value: string);
    /**
     * 获取轨迹点插值方式
     * @returns {string} 当前插值方式
     */
    get pointHandle(): string;
    _pointHandle: string | undefined;
    curvePath: any;
}
import { TrackerAbstract } from './TrackerAbstract';
