/**
 * 样条编辑器
 * ```js
 * const tile = engine.add(new mapvthree.Default3DTiles({
 *   url: tilesetUrl,
 * }))
 * tile.addEventListener('click', () => {});
 * const lineEditor = engine.add(new mapvthree.SplineEditor(engine, {
 *   virtualLineParameter: {
 *      color: 'red',
 *      lineWidth: 2,
 *   },
 *   lineParameter: {
 *      color: 'green',
 *      lineWidth: 5,
 *   }
 * }));
 * // 贴地绘制线
 * const lineEditor = engine.add(new mapvthree.SplineEditor(engine, {
 *    clampToGround: true,
 * }))
 * ```
 * ![](media://lineEditor.png)
 */
