    /** @private */
    private _data;
    /** @private */ private _engine;
    /** @private */ private _options;
    /** @private */ private _virtualLine;
    /** @private */ private _virtualLineDataSource;
    /** @private */ private _helpers;
    /** @private */ private _helpersDataSource;
    /** @private */ private _spline;
