export class DraggingMovementAggregator {
    constructor(options?: {});
    /** @private */
    private _lastTime;
    _dragEndTime: number;
    /** @private */
    private _inertiaPosition;
    _diffX: number;
    _diffY: number;
    _dir: any;
    _dragPoint: any;
    _lastDragPoint: any;
    _velocity: number;
    _lastDiffDistance: number;
    _amplitudeX: number;
    _amplitudeY: number;
    _targetX: number;
    _targetY: number;
    _scale: number;
    start(x: any, y: any, time: any): void;
    update(x: any, y: any, time: any): void;
    startInertia(isOperateRotation?: boolean): void;
    getInertiaPosition(time: any): any[];
    set scale(scale: number);
    get scale(): number;
}
