/**
 * 动画模型，支持GLTF/GLB等格式的骨骼动画播放与控制。
 * 继承自 {@link SimpleModel}，可用于加载带有动画的3D模型，并提供动画的播放、停止、速度、循环等控制接口。
 *
 * @category Model
 * @example
 * ```javascript
 * // 加载带动画的模型
 * let model = engine.add(new mapvthree.AnimationModel({
 *   url: 'assets/models/animated.glb',
 *   autoPlay: true
 * }));
 * // 播放第1个动画
 * model.play(0);
 * // 停止所有动画
 * model.stopAll();
 * // 设置动画速度为2倍
 * model.setSpeed(2);
 * ```
 */
export class AnimationModel extends SimpleModel {
    /**
     * 构造函数
     * @param {Object} params 配置参数，参考SimpleModel
     */
    constructor(params: Object);
    /**
     * 初始化动画数据，内部方法
     * @param {Object} gltf GLTF加载结果
     * @param {Object3D} model 模型对象
     * @internal
     */
    _updateData(gltf: Object, model: Object3D): void;
    animations: any;
    mixer: any;
    animationActions: any;
    /**
     * 播放指定索引的动画
     * @param {number} [actionIndex=0] 动画索引，默认播放第一个
     * @example
     * ```javascript
     * model.play(1); // 播放第2个动画
     * ```
     */
    play(actionIndex?: number): void;
    /**
     * 停止指定索引的动画
     * @param {number} [actionIndex=0] 动画索引，默认停止第一个
     * @example
     * ```javascript
     * model.stop(0); // 停止第1个动画
     * ```
     */
    stop(actionIndex?: number): void;
    /**
     * 播放所有动画
     * @example
     * ```javascript
     * model.playAll();
     * ```
     */
    playAll(): void;
    /**
     * 停止所有动画
     * @example
     * ```javascript
     * model.stopAll();
     * ```
     */
    stopAll(): void;
    /**
     * 设置动画播放速度
     * @param {number} speed 播放速度，1为正常速度
     * @param {number} [actionIndex] 动画索引，若不传则设置所有动画
     * @example
     * ```javascript
     * model.setSpeed(2); // 所有动画2倍速
     * model.setSpeed(0.5, 0); // 第1个动画0.5倍速
     * ```
     */
    setSpeed(speed: number, actionIndex?: number): void;
    /**
     * 设置动画循环模式
     * @param {boolean} loop 是否循环，true为循环，false为只播放一次
     * @param {number} [actionIndex] 动画索引，若不传则设置所有动画
     * @example
     * ```javascript
     * model.setLoop(true); // 所有动画循环
     * model.setLoop(false, 0); // 第1个动画只播放一次
     * ```
     */
    setLoop(loop: boolean, actionIndex?: number): void;
}
import { SimpleModel } from './SimpleModel';
