/**
 * 静态天空，预置了常见的天气状态和时段状态
 * 根据时间和天气自动切换不同的天空纹理贴图，提供基础的天空渲染效果。
 * 相比 {@link DynamicSky}，静态天空性能更好但效果相对简单。
 *
 * @category Natural
 * @example
 * ```javascript
 * // 创建静态天空
 * const sky = engine.add(new mapvthree.StaticSky());
 *
 * // 设置时间为下午5:30
 * sky.time = 3600 * 17.5;
 * ```
 */
export class StaticSky extends CustomStaticSky {
    /**
     * 标识为StaticSky对象
     * @type {boolean}
     * @readonly
     */
    readonly isStaticSky: boolean;
    /**
     * 天空名称
     * @type {string}
     * @readonly
     */
    readonly name: string;
    /** @internal */ weather: string;
    /** @internal */ _phase: string;
    /** @internal */ _lastPhase: null;
    /** @internal */ _lastWeather: null;
    /** @internal */ _nightStartTime: number;
    /** @internal */ _nightEndTime: number;
    /** @internal */ _afternoonTime: number;
    /** @internal */ _duskTime: number;
    /**
     * 本地变换处理，旋转天空纹理
     * @param {Object3D} mesh 天空网格对象
     * @internal
     */
    _onLocalTransform(mesh: Object3D): void;
    /**
     * 加载纹理用于天空，加载的纹理由天气和时间决定
     * 根据当前的时间段和天气状态，从预置的纹理资源中加载对应的天空贴图
     *
     * @private
     */
    private loadSkyTexture;
}
import { CustomStaticSky } from './CustomStaticSky';
